
public class BreakStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("pgm to break");
		for(int i=1;i<=10;i++)
		{
			if(i==5)
			{
				break;
				
			}
			System.out.println("i = +i");
		}
		System.out.println("=====");
		for(int i=1;i<=10;i++)
		{
			if(i==6)
			{
				break;
				
			}
			System.out.println("i = +i");
		}

	}

}
