
public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=456,count=0;

		  System.out.println("Enter a number: " +num);
		  {
		  for(num=456;num!=0;num=num/10)
		      count++;
		  }

		  System.out.println("Total digits is:  " +count);

		  

	}

}
