
public class UnaryOperation {

	public static void main(String[] args) {
		System.out.println("illustrating unary operation");
		int a=10;
		int preIncrNum=++a;
		System.out.println("value of preIncrNum is : " +preIncrNum);
		int b=10;
		int postIncrNum=b++;
		System.out.println("value of postIncrNum is : " +postIncrNum);
		System.out.println("the value of b is : " +b);
		// TODO Auto-generated method stub

	}

}
